SET NAMES utf8;

UPDATE `achievement_reward` SET `subject` = 'Félicitations', `text` = 'Toutes mes félicitations pour votre application à atteindre votre quatre-vingtième saison d\'aventures. Vous êtes indubitablement $gdévoué:dévouée; à la cause de l\'éradication du mal qui nous touche en Azeroth.\n\nEt bien que le voyage n\'ait pas été une mince affaire jusqu\'à présent, la véritable bataille nous attend encore.\n\nContinuez le combat !\n\nRhonin' WHERE `entry` = 13;
UPDATE `achievement_reward` SET `text` = 'Eh ben, vous !\n\nEt moi qui croyais avoir tout vu sur ces terres gelées ! Le feu de l\'exploration brûle en vous. Cela saute à mes yeux de nain.\n\nPortez ce tabard avec fierté. De cette façon, vos amis sauront toujours à qui demander leur chemin en temps voulu !\n\nRestez bien en selle !\n\nBrann Barbe-de-bronze' WHERE `entry` = 45;
UPDATE `achievement_reward` SET `subject` = 'Pour l\'Alliance !' WHERE `entry` = 614;
UPDATE `achievement_reward` SET `subject` = 'Pour la Horde !', `text` = 'En ces temps troublés, nos souffrances engendrent de vrais héros. Vous êtes l\'un d\'eux.\n\nNous sommes en guerre. Vos efforts soutiennent notre cause en Azeroth. Vos hautes actions seront récompensées. Prenez ce prix d\'Orgrimmar et chevauchez vers la gloire.\n\nPour la Horde !\n\nChef de guerre Thrall' WHERE `entry` = 619;
